CREATE TABLE airline (id BIGINT AUTO_INCREMENT, tag VARCHAR(2) UNIQUE, name VARCHAR(255), slug VARCHAR(255), UNIQUE INDEX airline_sluggable_idx (slug), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE area_translation (id BIGINT, name VARCHAR(255) NOT NULL, lang CHAR(5), slug VARCHAR(255), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE area (id BIGINT AUTO_INCREMENT, code VARCHAR(4) NOT NULL UNIQUE, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE city_translation (id BIGINT, name VARCHAR(255) NOT NULL, lang CHAR(5), slug VARCHAR(255), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE city (id BIGINT AUTO_INCREMENT, code VARCHAR(3) NOT NULL UNIQUE, airport VARCHAR(255), country_id BIGINT NOT NULL, state_id BIGINT, cache TINYINT(1) DEFAULT '0', archived TINYINT(1) DEFAULT '0', metropolitan TINYINT(1) DEFAULT '0', latitude DECIMAL(18, 15), longitude DECIMAL(18, 15), UNIQUE INDEX code_index_idx (code), INDEX airport_index_idx (airport), INDEX country_id_index_idx (country_id), INDEX state_id_index_idx (state_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE city_multiple_airport (city_id BIGINT, city_metro_id BIGINT, PRIMARY KEY(city_id, city_metro_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE city_metro (id BIGINT AUTO_INCREMENT, city_metro_id BIGINT UNIQUE NOT NULL, INDEX city_metro_id_idx (city_metro_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE country_translation (id BIGINT, name VARCHAR(255) NOT NULL, lang CHAR(5), slug VARCHAR(255), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE country (id BIGINT AUTO_INCREMENT, code VARCHAR(2) NOT NULL, area_id BIGINT NOT NULL, state TINYINT(1) DEFAULT '0', INDEX area_id_index_idx (area_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE hotel_translation (id BIGINT, short_description TEXT, long_description LONGTEXT, lang CHAR(5), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE hotel (id BIGINT AUTO_INCREMENT, hotel_id VARCHAR(255) NOT NULL UNIQUE, name VARCHAR(255) NOT NULL, baseimagelink VARCHAR(255), star_rating VARCHAR(10), address1 VARCHAR(255), address2 VARCHAR(255), postalcode VARCHAR(20), city VARCHAR(255), state_id BIGINT NOT NULL, country_id BIGINT NOT NULL, location_id BIGINT NOT NULL, parking TINYINT(1), restaurant TINYINT(1), internet TINYINT(1), pool TINYINT(1), fitness TINYINT(1), latitude DOUBLE(18, 2), longitude DOUBLE(18, 2), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, UNIQUE INDEX hotel_id_index_idx (hotel_id), INDEX hotel_index_idx (name, city), UNIQUE INDEX country_id_index_idx (country_id), INDEX state_id_idx (state_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE hotel_cities (hotel_id BIGINT, city_id BIGINT, PRIMARY KEY(hotel_id, city_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE hotel_facility_translation (id BIGINT, name VARCHAR(255) NOT NULL UNIQUE, lang CHAR(5), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE hotel_facility (id BIGINT AUTO_INCREMENT, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE promotional_banner_translation (id BIGINT, message TEXT NOT NULL, lang CHAR(5), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE promotional_banner (id BIGINT AUTO_INCREMENT, filename VARCHAR(255) NOT NULL, color_bg VARCHAR(10) DEFAULT '#FFFFFF', position VARCHAR(255), rank BIGINT NOT NULL, start_at DATETIME, expires_at DATETIME, published TINYINT(1) DEFAULT '0', archived TINYINT(1) DEFAULT '0', link VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE request_init_plex (id BIGINT AUTO_INCREMENT, date DATETIME NOT NULL, user_culture VARCHAR(10) NOT NULL, user_ip VARCHAR(20) NOT NULL, user_agent VARCHAR(255) NOT NULL, user_folder VARCHAR(255) NOT NULL, elapsed_time DECIMAL(18, 14) NOT NULL, header TEXT NOT NULL, response_code INT NOT NULL, response_raw LONGTEXT, stid VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE request_plex (id BIGINT AUTO_INCREMENT, date DATETIME NOT NULL, type VARCHAR(255) NOT NULL, search_infos TEXT NOT NULL, user_culture VARCHAR(10) NOT NULL, user_ip VARCHAR(20) NOT NULL, user_agent VARCHAR(255), user_folder VARCHAR(255) NOT NULL, filename VARCHAR(255) NOT NULL, user_info TEXT, header TEXT NOT NULL, header_raw TEXT, response_raw LONGTEXT, response_code INT NOT NULL, response_processed LONGTEXT, elapsed_plex_request DECIMAL(18, 14) NOT NULL, elapsed_process_response DECIMAL(18, 14) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE state_translation (id BIGINT, name VARCHAR(255) NOT NULL, lang CHAR(5), slug VARCHAR(255), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE state (id BIGINT AUTO_INCREMENT, code VARCHAR(3) NOT NULL UNIQUE, country_id BIGINT NOT NULL, INDEX country_id_index_idx (country_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE top_destination_translation (id BIGINT, name VARCHAR(255) NOT NULL, lang CHAR(5), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE top_destination (id BIGINT AUTO_INCREMENT, filename VARCHAR(255) NOT NULL, rank BIGINT NOT NULL, start_at DATETIME, expires_at DATETIME, published TINYINT(1) DEFAULT '0', archived TINYINT(1) DEFAULT '0', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
